import logo from './logo.svg';
import './App.css';

function App() {
  const [cart, setCart] = useState([]);

  const addToCart = (someProduct) => {
    setCart([...cart, product]);
  }
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1> 
          Cozy Threads
        </h1>
        <a className="App-link" href="#" onClick={(e) => {render('home'); }}>
          Home
        </a>
        <a className="App-link" href="#" onClick={(e) => {render('cart'); }}>
          Cart
        </a>
      </header>
      <main id="feed">
        <ProductFeed addToCart={addToCart} />
        <ProductCart cart={cart} />
        <Checkout cart={cart} />
      </main>
    </div>
  );
}

export default App;
