import { useCallback } from 'react';
import * as React from 'react';
import {loadStripe} from '@stripe/stripe-js';
import {
  EmbeddedCheckoutProvider,
  EmbeddedCheckout
} from '@stripe/react-stripe-js';
const stripePromise = loadStripe('pk_live_51PhD3vRpA6VhXFHy5CdMHlEl7CtPIOHZ2Fu7dQunAd3K469wh3QlBpjBdN1q3EAom5VC66VTihMoO9dvEH1abBvk00hqBsWmcq');

const Checkout = ({cart}) => {
    // const fetchClientSecret = useCallback(async () => {
    //     console.log("fetching client secret and sending this body: ", JSON.stringify({ items: cart }));
    //     return await fetch("http://localhost:4242/create-checkout-session", {
    //         method: "POST",
    //         // mode: "no-cors",
    //         headers: { //is this necessary?
    //             "Content-Type": "application/json"
    //         },
    //         body: JSON.stringify({ items: cart })
    //     })
    //     .then((res) => res.json())
    //     .then((data) => data.clientSecret);
    // }, [cart]);
    const fetchClientSecret = useCallback(async () => {
        console.log("fetching client secret and sending this body: ", JSON.stringify({ items: cart }));
        try {
          const response = await fetch("http://localhost:4242/create-checkout-session", {
            method: "POST",
            headers: {
              "Content-Type": "application/json"
            },
            body: JSON.stringify({ items: cart })
          });

          if (!response.ok) {
            throw new Error(`Server error: ${response.statusText}`);
          }

          const data = await response.json();
          return data.clientSecret;
        } catch (error) {
          console.error("Failed to fetch client secret:", error);
        }
      }, [cart]);

    const options = {fetchClientSecret};

    return (
        <div id="checkout">
            <EmbeddedCheckoutProvider
                stripe={stripePromise}
                options={options}
            >
                <EmbeddedCheckout/>
            </EmbeddedCheckoutProvider>
        </div>
    );
}

export default Checkout;
