import images from '../Images/Images'

const products = [
    {id: 1, image: images.blackTankTop, title: "Tank Top - Black", description: "Our High Quality Scoop Neck Tank Top is made from bamboo fibers sourced through eco-friendly methods", price: 10.99},
    {id: 2, image: images.blueHat, title: "Baseball Cap - Blue", description: "Our High Quality baseball cap is made from 100% cotton sourced through eco-friendly methods", price: 15.99},
    {id: 3, image: images.blackPants, title: "Straight Leg Pants - Black", description: "Our High Quality straight-legged pants are made from bamboo fibers and cotton sourced through eco-friendly methods", price: 24.99},
    {id: 4, image: images.pinkShirt, title: "Long Sleeve Crew Neck - Pink", description: "Our High Quality Long Sleeve Crew Neck is made from bamboo fibers and cotton sourced through eco-friendly methods", price: 20.99},
]

function ProductFeed({addToCart}) {
    return (
        <div id="home" className="page">
            <div id="productGrid" className="product-grid">
                {products.map(product => (
                    <div key={product.id} className="product">
                        <img src={product.image} alt={product.title} /> 
                        <div className="product-info">
                            <h3>{product.title}</h3>
                            {/* <div className='description-on-hover'> */}
                            <p>{product.description}</p>
                            {/* </div> */}
                            <p className="price">Price: ${product.price}</p>
                            <button onClick={() => addToCart(product)}>Add To Cart</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default ProductFeed;
