import React, { useState } from 'react';
import ProductCart from './Components/ProductCart.js';
import ProductFeed from './Components/ProductFeed.js';
import Checkout from './Components/Checkout.js';
import images from './Images/Images.js';
import './styles.css';
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { ToastContainer } from 'react-toastify';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';


function App() {
  const [cart, setCart] = useState([]);

  const addToCart = (product) => {
    setCart((prevCart) => {
      if (prevCart.find(item => item.id === product.id)) {
        return prevCart.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });
    toast('Item added to cart');
  }

  const removeFromCart = (product) => {
    setCart((prevCart) => {
      const productInCart = prevCart.find(item => item.id === product.id);
      if (productInCart.quantity === 1) {
        return prevCart.filter(item => item.id !== productInCart.id);
      } else {
        return prevCart.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity - 1 }
            : item
        );
      }
    });
  }

  function render(page) {
    document.querySelectorAll('.page').forEach(el => el.style.display = 'none');
    document.getElementById(page).style.display = 'block';
  }

  return (
    <Router basename="/cozy-threads">
      <div className="App">
        <header className="App-header">
          <div className='Title-Container'>
            <img src={images.logo} alt="Cozy Threads Logo" className="logo" />
            <h1 href="#" onClick={(e) => {render('home'); }}> Cozy Threads</h1>
          </div>
          <nav>
          <Link to="/">
              <img src={images.home} alt="Home Symbol"/>Store
            </Link>
            <Link to="/cart">
              <img src={images.cart} alt="Cart Symbol"/>Cart ({cart.reduce((total, item) => total + item.quantity, 0)})
            </Link>
          </nav>
        </header>
        <main id="feed">
            <ToastContainer/>       
            <Switch>
              <Route path="/" exact>
                <ProductFeed addToCart={addToCart} />
              </Route>
              <Route path="/cart">
                <ProductCart cart={cart} removeFromCart={removeFromCart} />
              </Route>
              <Route path="/checkout">
                <Checkout cart={cart} />
              </Route>
            </Switch>
        </main>
      </div>
    </Router>
  );
}

export default App;
