import React, { useState } from 'react';
import ProductCart from './Components/ProductCart.js';
import ProductFeed from './Components/ProductFeed.js';
import images from './Images/Images.js';
import './styles.css';
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { ToastContainer } from 'react-toastify';
import Success from './Components/Success.js';

function App() {
  const [cart, setCart] = useState([]);
  const [page, setPage] = useState('home');

  const addToCart = (product) => {
    setCart((prevCart) => {
      if (prevCart.find(item => item.id === product.id)) {
        return prevCart.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });
    toast('Item added to cart');
  }

  const removeFromCart = (product) => {
    setCart((prevCart) => {
      const productInCart = prevCart.find(item => item.id === product.id);
      if (productInCart.quantity === 1) {
        return prevCart.filter(item => item.id !== productInCart.id);
      } else {
        return prevCart.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity - 1 }
            : item
        );
      }
    });
  }

  function render(page) {
    document.querySelectorAll('.page').forEach(el => el.style.display = 'none');
    document.getElementById(page).style.display = 'block';
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className='Title-Container'>
          <img src={images.logo} alt="Cozy Threads Logo" className="logo" />
          <h1 href="#" onClick={(e) => {e.preventDefault(); setView('home');}}> Cozy Threads</h1>
        </div>
        <nav>
          <a href="/" onClick={(e) => {e.preventDefault(); setView('home'); }} onLoad={(e) => {e.preventDefault(); render('home'); }}>
            <img src={images.home} alt="Home Symbol"/>Store</a>
          <a href="#cart" onClick={(e) => {setView('cart'); }}>
            <img src={images.cart} alt="Cart Symbol"/>Cart ({cart.reduce((total, item) => total + item.quantity, 0)})</a>
        </nav>
      </header>
      <main id="feed">
          <ToastContainer/>       
          {view === 'home' && <ProductFeed addToCart={addToCart} />}
          {view === 'cart' && <ProductCart cart={cart} removeFromCart={removeFromCart} />}
          {view === 'success' && <Success />}
      </main>
    </div>
  );
}

export default App;
